mapper.select:
SELECT COUNT(*) FROM fb_ai_contents C JOIN fb_ai_user_contents UC ON C.content_seq = UC.content_seq WHERE C.delete_yn = 'N' AND UC.user_seq = get_user_seq_by_uuid(#{uuid})
AND ( C.title LIKE CONCAT('%', #{keyword}, '%') OR C.description LIKE CONCAT('%', #{keyword}, '%') OR EXISTS ( SELECT 1 FROM fb_ai_content_tags T WHERE T.content_seq = C.content_seq AND T.tag LIKE CONCAT('%', #{keyword}, '%') ) )
AND C.category = #{category}
AND C.process_status = #{status}

mapper.update:
UPDATE fb_ai_contents
SET
title = #{title},
description = #{description},
category = #{category}
, thumbnail_url = #{thumbnailUrl}
, duration = #{duration}
WHERE
content_seq = #{contentSeq}

UPDATE fb_ai_contents
SET delete_yn = 'Y'
WHERE content_seq = #{contentSeq}

UPDATE fb_ai_contents
SET
process_status = #{status}
, completed_at = #{completedAt}
WHERE
content_seq = #{contentSeq}

mapper.select:
SELECT
ORDER BY C.indate DESC
FROM fb_ai_contents C WHERE C.delete_yn = 'N' AND EXISTS ( SELECT 1 FROM fb_ai_content_tags T WHERE T.content_seq = C.content_seq AND T.tag = #{tag} )
AND ( C.title LIKE CONCAT('%', #{keyword}, '%') OR C.description LIKE CONCAT('%', #{keyword}, '%') )
AND C.category = #{category}
AND C.process_status = #{status}

SELECT COUNT(*) FROM fb_ai_contents C WHERE C.delete_yn = 'N' AND EXISTS ( SELECT 1 FROM fb_ai_content_tags T WHERE T.content_seq = C.content_seq AND T.tag = #{tag} )
AND ( C.title LIKE CONCAT('%', #{keyword}, '%') OR C.description LIKE CONCAT('%', #{keyword}, '%') )
AND C.category = #{category}
AND C.process_status = #{status}
