mapper.insert:
INSERT INTO fb_ai_chats (
    content_seq, user_seq, question, answer, indate
) VALUES (
    #{contentSeq}, #{userSeq}, #{question}, #{answer}, #{indate}
)

mapper.select:
SELECT chat_seq, question, answer, indate FROM fb_ai_chats WHERE content_seq = #{contentSeq} AND user_seq = #{userSeq}
AND indate < #{lastIndate}
ORDER BY indate DESC LIMIT #{limit}
