mapper.insert:
INSERT INTO fb_ai_chats (
    content_seq, user_seq, question, answer, indate
) VALUES (
    #{contentSeq}, #{userSeq}, #{question}, #{answer}, #{indate}
)

mapper.select:
SELECT
    chat_seq, content_seq, user_seq, question, answer, indate
FROM fb_ai_chats
WHERE content_seq = #{contentSeq}
  AND user_seq = #{userSeq}

      AND chat_seq < #{lastChatSeq}

ORDER BY chat_seq DESC
LIMIT #{limit}
